import React from 'react';

class Teams extends React.Component {
	state = {
	  tasks: []
	};

	handleSubmit = task => {
	  this.setState({tasks: [...this.state.tasks, task]});
	}

	handleDelete = (index) => {
	  const newArr = [...this.state.tasks];
	  newArr.splice(index, 1);
	  this.setState({tasks: newArr});
	}

	render() {
	  return(
		//<div className='rectangle'> 
		<div className='wrapper'>
		  <div className='card frame'>
		  	<Time />
			<Header numTodos={this.state.tasks.length} /> 
			<div className='spacing'>
			<TodoList tasks={this.state.tasks} onDelete={this.handleDelete} /> </div>
			<SubmitForm onFormSubmit={this.handleSubmit} />
		  </div>
		</div>
		//</div>
	  );
	} 
  }



  class SubmitForm extends React.Component {
	state = { term: '' };

	handleSubmit = (e) => {
	  e.preventDefault();
	  if(this.state.term === '') return;
	  this.props.onFormSubmit(this.state.term);
	  this.setState({ term: '' });
	}

	render() {
	  return(
		<form 
		style={{
			height:'0px',
			display: 'flex',
			justifyContent: 'Center',
			flexDirection: 'row',
			alignItems: 'Center',
			padding: '0px',
			width: '300px'
		}}
		onSubmit={this.handleSubmit}>
		  <input 
			type='text'
			className='input'
			placeholder='Enter Task'
			value={this.state.term}
			onChange={(e) => this.setState({term: e.target.value})}
		  />
		  <button className='button'>Submit</button>
		</form>
	  );
	}
  }


  const Header = (props) => {
	return(
	  <div className='card-header'>
		<h1 className='card-header-title header'>
		  Tasks left for today: {props.numTodos} 
		</h1>
	  </div>
	)
  }

  const Time = () => {
	const locale = 'en';
	const [today, setDate] = React.useState(new Date());
	React.useEffect(() => {
		const timer = setInterval(() => { // Creates an interval which will update the current data every minute
		// This will trigger a rerender every component that uses the useDate hook.
		setDate(new Date());
	  }, 60 * 1000);
	  return () => {
		clearInterval(timer); // Return a funtion to clear the timer so that it will stop being called on unmount
	  }
	}, []);
	return(
		<h1
		style ={{
			fontSize:80,
			display: 'flex',
			//justifyContent: 'Left',
			//alignContent: 'center',
			height: '6vh',
			width: '60vh',
			minHeight: '1vh'
		}}
		> {today.toLocaleTimeString(locale, { hour: 'numeric', hour12: true, minute: 'numeric' })} </h1>
	)
  }


  const TodoList = (props) => {
	const todos = props.tasks.map((todo, index) => {
	  return <Todo content={todo} key={index} id={index} onDelete={props.onDelete} />
	})
	return( 
	  <div className='list-wrapper'>
		{todos}
	  </div>
	);
  }

  const Todo = (props) => {
	return(
	  <div className='list-item'>
		{props.content}
		{'  '} 
		<button 
		style={{
			display: 'flex-align',
			height: '18px',
			backgroundColor: '#ff6661',
			color: '#4a4a4a',
			justifyContent: 'center',
  			alignItems: 'center',
			border: 'none',
			borderRadius: '100px',
		}}
		class="delete is-pulled-right" onClick={() => {props.onDelete(props.id)}}>x</button>
	  </div>
	);
  }


  export default Teams;
